#!/bin/zsh set -uo pipefail arg="$1"
run() { ./desk "$@" 2>&1 }
notify() { local msg="$1" /usr/bin/osascript -e "display notification \"${msg//\"/\\\"}\" with title \"Desk\"" }
if [[ "$arg" == __set__:* ]]; then rest="${arg#__set__:}" name="${rest%:*}" num="${rest##*:}" if ! out="$(run set "$name" "$num")"; then notify "$out" exit 1 fi exit 0 fi
if [[ "$arg" == __rm__:* ]]; then name="${arg#__rm__:}" if ! out="$(run rm "$name")"; then notify "$out" exit 1 fi exit 0 fi
[[ "$arg" == "__noop__" ]] && exit 0
if ! out="$(run go "$arg")"; then notify "$out" exit 1 fi

